SELECT rev_code, mbsp_id, pro_num, rev_title, rev_content, rev_rate, rev_date FROM ( SELECT /*+ INDEX_DESC(product_tbl PK_PRO_NUM) */ ROWNUM AS RN, rev_code, mbsp_id, pro_num, rev_title, rev_content, rev_rate, rev_date review_tbl WHERE pro_num = #{pro_num} AND
ROWNUM <= (#{cri.pageNum} * #{cri.amount}) -- 페이지번호 * 페이지별 출력건수
) WHERE RN > (#{cri.pageNum} - 1 ) * #{cri.amount}
